select registro.idRegistro,registro.usuarioRegistro, registro.estadoRegistro,registro.fechaRegistro,plantas.nameplanta, transportador.tipoTransportador,rodilloCarga,rodilloImpacto,estadoRodilloRetorno,count(transportador.idTransportador) as 'Cantidad de Rodillos',anguloAcanalamientoArtesa1, anguloAcanalamientoArtesa2, anguloAcanalamientoArtesa3, anguloAcanalamientoArtesa1AntesPoleaMotriz, anguloAcanalamientoArtesa2AntesPoleaMotriz,anguloAcanalamientoArtesa3AntesPoleaMotriz,largoTuboRodilloCentralCarga,largoTuboRodilloLateralCarga,largoTuboRodilloRetorno,anchoBandaTransportadora,icobandasCentradaEnPoleaTensora,icobandasCentradaPoleaAmarrePoleaCola,icobandasCentradaPoleaAmarrePoleaMotriz,icobandasCentradaPoleaColaTransportadora,icobandasCentraEnPoleaMotrizTransportadora,count(transportador.idTransportador) as 'Cantidad de Equipos'

from registro
join plantas on registro.codplanta=plantas.codplanta
join transportador on transportador.idTransportador=registro.idTransportador
join bandaTransportadora on registro.idRegistro=bandaTransportadora.idRegistro
where transportador.tipoTransportador='B.T'

group by registro.usuarioRegistro,registro.idRegistro,registro.estadoRegistro,registro.fechaRegistro, plantas.nameplanta, transportador.tipoTransportador,
transportador.tipoTransportador,rodilloCarga,rodilloImpacto,estadoRodilloRetorno,anguloAcanalamientoArtesa1, anguloAcanalamientoArtesa2, anguloAcanalamientoArtesa3, anguloAcanalamientoArtesa1AntesPoleaMotriz,anguloAcanalamientoArtesa2AntesPoleaMotriz,anguloAcanalamientoArtesa3AntesPoleaMotriz,largoTuboRodilloCentralCarga,largoTuboRodilloLateralCarga,largoTuboRodilloRetorno,anchoBandaTransportadora,icobandasCentradaEnPoleaTensora,icobandasCentradaPoleaAmarrePoleaCola,icobandasCentradaPoleaAmarrePoleaMotriz,icobandasCentradaPoleaColaTransportadora,icobandasCentraEnPoleaMotrizTransportadora